import {
  isProgramError,
  type Address,
  type SOLANA_ERROR__INSTRUCTION_ERROR__CUSTOM,
  type SolanaError,
} from "@solana/kit";
import { VAULT_PROGRAM_ADDRESS } from "../programs";

/** AlreadyClaimed: Deposit already claimed */
export const VAULT_ERROR__ALREADY_CLAIMED = 0x1770; // 6000
/** InvalidSecret: Invalid secret code */
export const VAULT_ERROR__INVALID_SECRET = 0x1771; // 6001
/** InvalidAmount: Invalid amount */
export const VAULT_ERROR__INVALID_AMOUNT = 0x1772; // 6002

export type VaultError =
  | typeof VAULT_ERROR__ALREADY_CLAIMED
  | typeof VAULT_ERROR__INVALID_AMOUNT
  | typeof VAULT_ERROR__INVALID_SECRET;

let vaultErrorMessages: Record<VaultError, string> | undefined;
if (process.env.NODE_ENV !== "production") {
  vaultErrorMessages = {
    [VAULT_ERROR__ALREADY_CLAIMED]: `Deposit already claimed`,
    [VAULT_ERROR__INVALID_AMOUNT]: `Invalid amount`,
    [VAULT_ERROR__INVALID_SECRET]: `Invalid secret code`,
  };
}

export function getVaultErrorMessage(code: VaultError): string {
  if (process.env.NODE_ENV !== "production") {
    return (vaultErrorMessages as Record<VaultError, string>)[code];
  }

  return "Error message not available in production bundles.";
}

export function isVaultError<TProgramErrorCode extends VaultError>(
  error: unknown,
  transactionMessage: {
    instructions: Record<number, { programAddress: Address }>;
  },
  code?: TProgramErrorCode,
): error is SolanaError<typeof SOLANA_ERROR__INSTRUCTION_ERROR__CUSTOM> &
  Readonly<{ context: Readonly<{ code: TProgramErrorCode }> }> {
  return isProgramError<TProgramErrorCode>(
    error,
    transactionMessage,
    VAULT_PROGRAM_ADDRESS,
    code,
  );
}
